mapper.select:
select distinct clinic_date appdate, (decode((select substr(jzsjqy,1,5) from gh_jzsjsz where pbjlxh=d.pbjlxh and ksghxh <= d.yyghxh and jsghxh >= d.yyghxh and ghsjlb=d.ghsjlb),'',decode(d.ghsjlb,1,'08:00','13:00'), (select substr(jzsjqy,1,5) from gh_jzsjsz where pbjlxh=d.pbjlxh and ksghxh <= d.yyghxh and jsghxh >= d.yyghxh and ghsjlb=d.ghsjlb))) ||'~' || ( decode((select substr(jzsjqy,7,5) from gh_jzsjsz where pbjlxh=d.pbjlxh and ksghxh <= d.yyghxh and jsghxh >= d.yyghxh and ghsjlb=d.ghsjlb),'',decode(d.ghsjlb,1,'11:30','16:30'), (select substr(jzsjqy,7,5) from gh_jzsjsz where pbjlxh=d.pbjlxh and ksghxh <= d.yyghxh and jsghxh >= d.yyghxh and ghsjlb=d.ghsjlb))) deadline, d."PBMXXH", d."YYGHXH", d."YYBZ" from MZYY_SCHEDUL ,"GH_YYPBMX" d where d.pbjlxh = jlxh
and d.PBJLXH =#{schid}
and d.ghsjlb = #{ampm}
and clinic_date = #{schdateStr}

select (decode((select substr(jzsjqy,1,5) from gh_jzsjsz where pbjlxh=d.pbjlxh and ksghxh <= d.yyghxh and jsghxh >= d.yyghxh and ghsjlb=d.ghsjlb),'',decode(d.ghsjlb,1,'08:00','13:00'), (select substr(jzsjqy,1,5) from gh_jzsjsz where pbjlxh=d.pbjlxh and ksghxh <= d.yyghxh and jsghxh >= d.yyghxh and ghsjlb=d.ghsjlb))) ||'~'||( decode((select substr(jzsjqy,7,5) from gh_jzsjsz where pbjlxh=d.pbjlxh and ksghxh <= d.yyghxh and jsghxh >= d.yyghxh and ghsjlb=d.ghsjlb),'',decode(d.ghsjlb,1,'11:30','16:30'), (select substr(jzsjqy,7,5) from gh_jzsjsz where pbjlxh=d.pbjlxh and ksghxh <= d.yyghxh and jsghxh >= d.yyghxh and ghsjlb=d.ghsjlb))) deadline , d."PBMXXH", d."YYGHXH", d."YYBZ" from "GH_YYPBMX" d where 1=1
and d.PBJLXH =#{schid}
and d.ghsjlb = #{ampm}

select m.clinic_date appdate, m.jlxh as pbCode, decode(m.clinic_type,'普通','1','专家','2','名医','3','2') schType, m.am_pm as timeType, m.regfee,m.treatfee , m.dept_code as depCode, m.doctor_code as docCode, g.xm,g.xb,c.mc,s.ksmc from MZYY_SCHEDUL m left join gy_zgxx g on m.DOCTOR_CODE = g.zgid left join (select * from gy_gydm where dmlb = 13) c on g.zc = c.dm left join gy_ksdm s on s.ksdm = m.dept_code where s.zfpb = 0
and m.DEPT_CODE = #{platdeptid}
group by m.clinic_date, m.jlxh,decode(m.clinic_type,'普通','1','专家','2','名医','3','2'), m.am_pm, m.regfee,m.treatfee , m.dept_code, m.doctor_code, g.xm,g.xb,c.mc,s.ksmc

select ghxh
from gh_mzyyk where pbxh = #{schid} and yysj = #{ampm}
and zt in (0,1) and to_char(yyrq1,'yyyy-mm-dd')=#{schdateStr}

select ghxh
from Gh_Ghk where ksdm = #{ksdm} and sjlb = #{ampm} and mzlbxh=#{mzlbxh}
and to_char(ghrq,'yyyy-mm-dd')=to_char(sysdate,'yyyy-mm-dd') and thbz=0



        and ysgh=#{ysgh}


        and ysgh is null

SELECT dul.MZJLXH jlxh,dul.clinic_fee FROM  GH_SCHEDUL dul WHERE
dul.dept_code = #{ksdm} and  dul.am_pm = #{ghsjlb}
   and  dul.doctor_code = #{ysgh} 
and  dul.doctor_code is NULL
