mapper.select:
select distinct a.yyxh,a.zt, to_char(a.djrq,'yyyyMMddhh24miss')mzrq ,to_char(a.yyrq2,'yyyyMMddhh24miss')jzrq,NVL(a.jzkh,c.jzkh) jzkh from gh_mzyyk a left join gh_ghk b on a.jzkh=b.jzkh left join zj_jzxx c on b.jzxh=c.jzxh where 1=1
AND a.yyxh=#{orderid}
and a.djrq BETWEEN to_date(#{bdate},'yyyy-MM-dd') and to_date(#{edate},'yyyy-MM-dd')

select yk.yyxh,yk.lxdh,yk.xm,yk.sfzh,yk.pbxh,yk.ksdm,yk.ysgh,yk.zt,yk.ghxh, yk.checkid,yk.jzkh, zg.xm ysxm,ml.zlf,ml.ghf,ks.ksmc,gh.jylsh,yk.yysj ,yk.yyrq1,yk.yyrq1,yk.numid, to_char(yk.djrq,'yyyy-mm-dd hh24:mi:ss')djrq from GH_MZYYK yk left join gy_ksdm ks on yk.ksdm = ks.ksdm left join gy_zgxx zg on yk.ysgh = zg.zgid left join gh_mzlbk ml on yk.mzlbxh =ml.mzlbxh left join gh_ghk gh on gh.jlxh = yk.jlxh where yk.sfzh = #{sfzh} and yk.yyrq1 > sysdate - 91
and yk.zt = #{zt}
and yk.yyxh = #{orderid}

select yk.yyxh,yk.lxdh,yk.xm,yk.sfzh,yk.pbxh,yk.ksdm,yk.ysgh,yk.zt,yk.ghxh, yk.checkid,yk.jzkh, zg.xm ysxm,ml.zlf,ml.ghf,ks.ksmc,gh.jylsh,yk.yysj ,yk.yyrq1,zj.jzrq,yk.yyrq1,yk.numid, to_char(yk.djrq,'yyyy-mm-dd hh24:mi:ss')djrq from GH_MZYYK yk left join gy_ksdm ks on yk.ksdm = ks.ksdm left join gy_zgxx zg on yk.ysgh = zg.zgid left join gh_mzlbk ml on yk.mzlbxh =ml.mzlbxh left join gh_ghk gh on gh.jlxh = yk.jlxh left join zj_jzxx zj on zj.jlxh =yk.jlxh where yk.sfzh = #{sfzh} and yk.yyrq1 > sysdate - 91
and yk.zt = #{zt}
and yk.yyxh = #{orderid}
